"use strict"

// Update this array if you add/rename/remove files in this directory.
// We support Browserify by skipping automatic module discovery and requiring modules directly.
var modules = [
    require("body-parser/node_modules/iconv-lite/encodings/internal"),
    require("body-parser/node_modules/iconv-lite/encodings/utf16"),
    require("body-parser/node_modules/iconv-lite/encodings/utf7"),
    require("body-parser/node_modules/iconv-lite/encodings/sbcs-codec"),
    require("body-parser/node_modules/iconv-lite/encodings/sbcs-data"),
    require("body-parser/node_modules/iconv-lite/encodings/sbcs-data-generated"),
    require("body-parser/node_modules/iconv-lite/encodings/dbcs-codec"),
    require("body-parser/node_modules/iconv-lite/encodings/dbcs-data"),
];

// Put all encoding/alias/codec definitions to single object and export it. 
for (var i = 0; i < modules.length; i++) {
    var module = modules[i];
    for (var enc in module)
        if (Object.prototype.hasOwnProperty.call(module, enc))
            exports[enc] = module[enc];
}
